		if ($deep) {
			if ($this->colltwPluginI18ns) {
				foreach ((array) $this->colltwPluginI18ns as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->colltwPluginModules) {
				foreach ((array) $this->colltwPluginModules as $o) {
