	// symfony_i18n behavior
	
	/**
	 * Returns the culture.
	 *
	 * @return string The culture
	 */
	public function getCulture()
	{
	  return $this->culture;
	}
	
	/**
	 * Sets the culture.
	 *
	 * @param string  The culture to set
	 *
	 * @return twPlugin
	 */
	public function setCulture($culture)
	{
	  $this->culture = $culture;
	  return $this;
	}
	
	/**
	 * Returns the "name" value from the current {@link twPluginI18n}.
	 */
	public function getName($culture = null)
	{
	  return $this->getCurrenttwPluginI18n($culture)->getName();
	}
	
	/**
	 * Sets the "name" value of the current {@link twPluginI18n}.
	 *
	 * @return twPlugin
	 */
	public function setName($value, $culture = null)
	{
	  $this->getCurrenttwPluginI18n($culture)->setName($value);
	  return $this;
	}
	
	/**
	 * Returns the "description" value from the current {@link twPluginI18n}.
	 */
	public function getDescription($culture = null)
	{
	  return $this->getCurrenttwPluginI18n($culture)->getDescription();
	}
	
	/**
	 * Sets the "description" value of the current {@link twPluginI18n}.
	 *
	 * @return twPlugin
	 */
	public function setDescription($value, $culture = null)
	{
	  $this->getCurrenttwPluginI18n($culture)->setDescription($value);
	  return $this;
	}
	
	/**
	 * Returns the current translation.
	 *
	 * @return twPluginI18n
	 */
	public function getCurrenttwPluginI18n($culture = null)
	{
	  if (null === $culture)
	  {
	    $culture = null === $this->culture ? sfPropel::getDefaultCulture() : $this->culture;
	  }
	
	  if (!isset($this->current_i18n[$culture]))
	  {
	    $object = $this->isNew() ? null : twPluginI18nPeer::retrieveByPK($this->getPrimaryKey(), $culture);
	    if ($object)
	    {
	      $this->settwPluginI18nForCulture($object, $culture);
	    }
	    else
	    {
	      $this->settwPluginI18nForCulture(new twPluginI18n(), $culture);
	      $this->current_i18n[$culture]->setCulture($culture);
	    }
	  }
	
	  return $this->current_i18n[$culture];
	}
	
	/**
	 * Sets the translation object for a culture.
	 */
	public function settwPluginI18nForCulture(twPluginI18n $object, $culture)
	{
	  $this->current_i18n[$culture] = $object;
	  $this->addtwPluginI18n($object);
	}

	/**
	 * Catches calls to virtual methods
	 */
	public function __call($name, $params)
	{
		// symfony_behaviors behavior
		if ($callable = sfMixer::getCallable('BasetwPlugin:' . $name))
		{
		  array_unshift($params, $this);
		  return call_user_func_array($callable, $params);
		}

		if (preg_match('/get(\w+)/', $name, $matches) && $this->hasVirtualColumn($matches[1])) {
			return $this->getVirtualColumn($matches[1]);
		}
		throw new PropelException('Call to undefined method: ' . $name);
	}

